import './App.css';
import React, { useEffect, useState } from 'react';


function App() {
  const [input, setInput] = useState('Henry')

  const fetchInput = async (input) => {
    setInput(input);
    const source = new EventSource(
      `http://localhost:4000/api?input=${input}`, 
      { withCredentials: true }
    );
    console.log(source)
    console.log("Proceeds");
  };

  const fetchExample = async () => {
    const source = new EventSource(
      `http://localhost:4000/ex-async`,
      { withCredentials: true }
    );
    source.addEventListener('message', (message) => {
      console.log("Got", message.data);
      let msg_obj = JSON.parse(message.data);
      console.log("Got (json)", msg_obj);
    });
    console.log("Proceeds");
  }

  return (
    <div className="App">
      <form>
        <input 
          type="text"
          value={input}
          onChange={(event) => {setInput(event.target.value)}}
        />
      </form>
      <button 
        type="button"
        onClick={() => {fetchInput(input)}}
        >Click
      </button>
      <button 
        type="button"
        onClick={() => {fetchExample()}}
        >Click-2
      </button>
    </div>
  );
}

export default App;
